INSERT INTO messages VALUES('2024-11-18 09:06:56.423Z','nhkh4jpepwbb4d5','quel est le programme de la formation','Le programme de la formation DevOps à O''clock est conçu pour développer les compétences nécessaires en automatisation, déploiement, supervision d''infrastructures et d''applications dans des environnements cloud. Il couvre des domaines tels que le déploiement automatisé, le développement frontend et backend, l''utilisation de conteneurs, la gestion de réseaux, l''infrastructure de serveurs, les chaines de livraison, la sécurité des flux entrants, les fournisseurs de cloud, l''infrastructure as code, l''orchestration et la supervision d''applications distribuées. Chaque saison de la formation aborde des compétences spécifiques, allant de l''algorithme à l''utilisation avancée des outils comme Docker, Ansible, Kubernetes, Terraform et bien d''autres. En fin de formation, les apprenants réalisent un projet autonome pour mettre en pratique l''ensemble des compétences acquises.',0,'2024-11-18 12:18:52.337Z','{"id":"1307995446941057084","channel_id":"1295315391425282178","guild_id":"1295315390901125121","content":"!question quel est le programme de la formation","timestamp":"2024-11-18T09:06:55.931Z","edited_timestamp":null,"mention_roles":[],"tts":false,"mention_everyone":false,"author":{"id":"968061362624426004","email":"","username":"keving.oclock","avatar":"2269870b3fec7a1bcda0e776a9a35292","locale":"","discriminator":"0","global_name":"Kévin G. O''clock","token":"","verified":false,"mfa_enabled":false,"banner":"","accent_color":0,"bot":false,"public_flags":0,"premium_type":0,"system":false,"flags":0},"attachments":[],"embeds":[],"mentions":[],"reactions":null,"pinned":false,"type":0,"webhook_id":"","member":{"guild_id":"","joined_at":"2024-11-15T09:41:08.626Z","nick":"","deaf":false,"mute":false,"avatar":"","user":null,"roles":["1306948845120393227","1306972263987417191"],"premium_since":null,"flags":0,"pending":false,"permissions":"0","communication_dis